export default function validation(data, pasos){
    let errors={};

    // if(!data.name){
    //     errors.name = "Este campo no puede estar vacio"
    // }
    if(!data.image){
        errors.image = "Este campo no puede estar vacio "
    }
    if(!data.summary){
        errors.summary = "Este campo no puede estar vacio"
    }
    // if(pasos.length ===0){
    //     errors.pasos = "Debe haber al menos un paso añadido"
    // }
    if((/^(https?:\/\/)?([\w.-]+)\.([a-z]{2,})(\/\S*)?$/i).test(data.image)){
        errors.image = "Debe ingresar una URL"
    }
    if((/^-?\d+$/).test(data.health_Score) && data.health_Score < 0 || data.health_Score >100){
        errors.health_Score = "Debe ser un número entre 0 y 100"
    }
}
