import axios from 'axios'


export function getRecipes(){
    
    return async (dispatch) =>{
        const data = (
            await axios.get('http://localhost:3001/recipes')).data
        dispatch({
            type: 'GET_RECIPES',
            payload: data
        })
        
    }
}
