import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { nextPage, prevPage } from '../../Redux/actions';
import './Paginado.css'

export default function Paginado({cantPages}) {
    const {pagActual} = useSelector((state)=>state);
    const dispatch = useDispatch();

    function nextP(){
        dispatch(nextPage());
    }

    function prevP(){
        dispatch(prevPage());
    }

    function irPage(e){
      dispatch(irPage(e.target.value))
    }

  return (
    <div >
      <div className='paginado'>
          {pagActual > 1 ? (
          <div>
            <button onClick={prevP}>PREV</button>
            <div>
            <a onClick={irPage}>{pagActual - 1}</a>
            </div>
          </div>
        ) : null}
        
        <a>{pagActual}</a>
        {pagActual < cantPages ? (
          <div>
            <a>{pagActual + 1}</a>
            <div>
            <button onClick={nextP}>NEXT</button>
            </div>
          </div>
        ) : null}
      </div>
    </div>
  )
}
