import axios from 'axios'


export function getRecipes(){
    return (dispatch)=>{
        axios.get(`http://localhost:3001/recipes`).then(({data})=>{      
            dispatch({
                type: 'GET_RECIPES',
                payload: data    
            })
        })    
    }
}
