import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { nextPage, prevPage, irPage } from '../../Redux/actions';
import './Paginado.css'

export default function Paginado({cantPages}) {
    const {pagActual} = useSelector((state)=>state);
    const dispatch = useDispatch();

    function nextP(){
        dispatch(nextPage());
    }

    function prevP(){
        dispatch(prevPage());
    }

    function irPag(p){
      dispatch(irPage(p))
      
    }

  return (
    
      <div className='paginado'>
          {pagActual > 1 ? (
          <div>
            <button onClick={prevP}>PREV</button>
            {pagActual>2 ?(
               <a onClick={()=>irPag(pagActual-2)}>{pagActual -2}</a>
            ):null}
            <a onClick={()=>irPag(pagActual-1)}>{pagActual -1}</a>
            
          </div>
        ) : null}
        
        <a style={{color: 'red'}} onClick={()=>irPag(pagActual)}>{pagActual}</a>
        {pagActual < cantPages ? (
          <div>
            <div>
            <a onClick={()=>irPag(pagActual+1)}>{pagActual +1}</a>
            {pagActual< cantPages-1 ?(
               <a onClick={()=>irPag(pagActual+2)}>{pagActual +2}</a>
            ):null}
            <button onClick={nextP}>NEXT</button>
            </div>
          </div>
        ) : null}
      </div>
    
  )
}
